def filter_closing_soon(markets: list, hours: int = HOURS_THRESHOLD) -> list:
    """Filter markets closing within the specified hours."""
    now = datetime.now(timezone.utc)
    closing_soon = []

    for market in markets:
        close_time = market.close_time
        if isinstance(close_time, str):
            close_time = datetime.fromisoformat(close_time.replace("Z", "+00:00"))

        time_until_close = close_time - now
        hours_until_close = time_until_close.total_seconds() / 3600

        if 0 < hours_until_close <= hours:
            closing_soon.append({
                "market": market,
                "hours_until_close": hours_until_close,
                "close_time": close_time
            })

    # Sort by time until close (soonest first)
    closing_soon.sort(key=lambda x: x["hours_until_close"])
    return closing_soon

